def text(draw, text, x, y, color):
    for char in text:
        ord_char = ord(char)
        if FIRST <= ord_char <= LAST:
            idx = ord_char - FIRST
        else:
            idx = 95 # Espacio o cuadrado si no existe
        
        # Cada caracter son 32 bytes (16 filas * 2 bytes por fila)
        # 16 pixeles de ancho requieren 2 bytes (16 bits)
        char_data = _FONT[idx*32:(idx+1)*32]
        
        for row in range(16):
            # Combinamos dos bytes para formar la fila de 16 bits
            # Byte 1: pixeles 0-7, Byte 2: pixeles 8-15
            byte1 = char_data[row*2]
            byte2 = char_data[row*2 + 1]
            row_bits = (byte1 << 8) | byte2
            
            for col in range(16):
                # Verificamos bit a bit (de izquierda a derecha, bit 15 al 0)
                if (row_bits >> (15-col)) & 1:
                    draw.pixel(x + col, y + row, color)
        
        x += 16
